tests:
from solution import longest_palindrome

def test_example_cases():
    assert longest_palindrome("babad") == "bab" or longest_palindrome("babad") == "aba"
    assert longest_palindrome("cbbd") == "bb"

def test_single_character():
    assert longest_palindrome("a") == "a"

def test_two_identical_characters():
    assert longest_palindrome("aa") == "aa"

def test_non_palindromic_string():
    assert longest_palindrome("abc") == "a" or longest_palindrome("abc") == "b" or longest_palindrome("abc") == "c"

def test_entire_string_palindrome():
    assert longest_palindrome("racecar") == "racecar"

def test_palindrome_within_string():
    assert longest_palindrome("xabax") == "xabax"
    assert longest_palindrome("xababa") == "ababa"

def test_long_mixed_string():
    assert longest_palindrome("abcdefgabcba") == "abcba"
    assert longest_palindrome("abacdfgdcaba") == "aba"
