from collections import deque

def is_path_possible(n, m, blocked_cells):
    if not blocked_cells:
        return True
    blocked = set(blocked_cells)
    start = (0, 0)
    end = (n-1, m-1)

    if start in blocked or end in blocked:
        return False

    queue = deque()
    queue.append(start)
    visited = set()
    visited.add(start)

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        current = queue.popleft()
        if current == end:
            return True
        for dr, dc in directions:
            new_r = current[0] + dr
            new_c = current[1] + dc
            if 0 <= new_r < n and 0 <= new_c < m:
                if (new_r, new_c) not in blocked and (new_r, new_c) not in visited:
                    visited.add((new_r, new_c))
                    queue.append((new_r, new_c))

    return False

