from solution import is_path_possible

def test_no_path_due_to_blockage():
    assert is_path_possible(3, 3, [(1, 2), (2, 2), (3, 2)]) == False

def test_path_exists():
    assert is_path_possible(3, 3, [(1, 2), (2, 2)]) == True

def test_no_blockages():
    assert is_path_possible(3, 3, []) == True

def test_start_is_blocked():
    assert is_path_possible(3, 3, [(1, 1), (2, 2)]) == False

def test_end_is_blocked():
    assert is_path_possible(3, 3, [(3, 3)]) == False

def test_smallest_grid_blocked():
    assert is_path_possible(1, 1, [(1, 1)]) == False

def test_smallest_grid_unblocked():
    assert is_path_possible(1, 1, []) == True

def test_large_grid_with_one_blockage():
    assert is_path_possible(1000, 1000, [(500, 500)]) == True

def test_no_path_in_large_blocked_grid():
    blocked_cells = [(i, 1) for i in range(1, 1001)]
    assert is_path_possible(1000, 1000, blocked_cells) == False
