from collections import deque

def is_path_possible(n, m, blocked_cells):
    # Check if start or end is blocked
    if (1, 1) in blocked_cells or (n, m) in blocked_cells:
        return False

    # Special case where grid is 1x1
    if n == 1 and m == 1:
        return True

    # Directions: up, down, left, right
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    visited = set()
    queue = deque()
    visited.add((1, 1))
    queue.append((1, 1))

    while queue:
        i, j = queue.popleft()

        # Explore all four directions
        for dx, dy in directions:
            ni, nj = i + dx, j + dy
            if 1 <= ni <= n and 1 <= nj <= m:
                if (ni, nj) == (n, m):
                    return True
                if (ni, nj) not in visited and (ni, nj) not in blocked_cells:
                    visited.add((ni, nj))
                    queue.append((ni, nj))

    return False

