from collections import deque

def is_path_possible(n, m, blocked_cells):
    if n == 0 or m == 0:
        return False  # Although constraints say n and m are at least 1

    blocked = set(blocked_cells)
    visited = [[False] * (m + 1) for _ in range(n + 1)]
    visited[1][1] = True

    queue = deque()
    queue.append((1, 1))

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        i, j = queue.popleft()
        for di, dj in directions:
            ni = i + di
            nj = j + dj
            if 1 <= ni <= n and 1 <= nj <= m:
                if (ni, nj) not in blocked and not visited[ni][nj]:
                    visited[ni][nj] = True
                    queue.append((ni, nj))

    return visited[n][m]

