from collections import deque

def is_path_possible(n, m, blocked_cells):
    if n == 0 or m == 0:
        return False

    blocked = set(blocked_cells)
    start = (0, 0)
    end = (n - 1, m - 1)

    if start in blocked or end in blocked:
        return False

    visited = [[False for _ in range(m)] for _ in range(n)]
    queue = deque()
    queue.append(start)
    visited[0][0] = True

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        x, y = queue.popleft()

        for dx, dy in directions:
            nx = x + dx
            ny = y + dy

            if 0 <= nx < n and 0 <= ny < m:
                if not visited[nx][ny] and (nx, ny) not in blocked:
                    visited[nx][ny] = True
                    queue.append((nx, ny))

    return visited[end[0]][end[1]]

