def maxSum(nums):
    if not nums:
        return 0
    n = len(nums)
    include = [0] * n
    exclude = [0] * n

    include[0] = nums[0]
    exclude[0] = 0

    for i in range(1, n):
        include[i] = nums[i] + exclude[i-1]
        exclude[i] = max(include[i-1], exclude[i-1])

    return max(include[-1], exclude[-1])
