def main():
    import sys
    input = sys.stdin.read().splitlines()
    idx = 0
    N = int(input[idx])
    idx += 1
    inventory = {}
    for _ in range(N):
        name, qty = input[idx].split()
        inventory[name] = int(qty)
        idx += 1
    M = int(input[idx])
    idx += 1
    orders = []
    for _ in range(M):
        name, qty = input[idx].split()
        orders.append((name, int(qty)))
        idx += 1
    for name, qty in orders:
        if name in inventory:
            inventory[name] -= qty
            if inventory[name] < 0:
                inventory[name] = 0
    for item in inventory:
        print(f"{item} {inventory[item]}")

if __name__ == "__main__":
    main()
