def compress(input_string):
    if not input_string:
        return ""

    groups = []
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            groups.append((current_char, count))
            current_char = char
            count = 1
    groups.append((current_char, count))

    compressed = []
    for char, cnt in groups:
        if cnt == 1:
            compressed.append(char)
        else:
            compressed.append(f"{char}{cnt}")
    compressed_str = ''.join(compressed)

    if len(compressed_str) < len(input_string):
        return compressed_str
    else:
        return input_string
