def compress(input_string):
    if not input_string:
        return input_string

    result = []
    current = input_string[0]
    count = 1

    for c in input_string[1:]:
        if c == current:
            count += 1
        else:
            result.append((current, count))
            current = c
            count = 1

    result.append((current, count))

    compressed = []
    for char, count in result:
        if count == 1:
            compressed.append(char)
        else:
            compressed.append(f"{char}{count}")

    compressed_str = ''.join(compressed)

    if len(compressed_str) < len(input_string):
        return compressed_str
    else:
        return input_string
