def compress(input_string):
    if not input_string:
        return input_string

    result = []
    current_char = input_string[0]
    count = 1

    for c in input_string[1:]:
        if c == current_char:
            count += 1
        else:
            result.append(current_char + str(count))
            current_char = c
            count = 1
    result.append(current_char + str(count))

    compressed_str = ''.join(result)

    if len(compressed_str) < len(input_string):
        return compressed_str
    else:
        return input_string
