import sys
from collections import Counter

def analyze_frequency(s):
    freq = Counter(s)
    max_freq = max(freq.values())
    most_common = [char for char, count in freq.items() if count == max_freq]
    most_common_sorted = sorted(most_common)
    return ' '.join(most_common_sorted)

def main():
    lines = []
    while True:
        line = sys.stdin.readline().strip()
        if line == '.':
            break
        lines.append(line)

    for s in lines:
        result = analyze_frequency(s)
        print(result)

if __name__ == "__main__":
    main()

