def letter_frequency_analyzer():
    lines = []
    while True:
        line = input().strip('\n')
        lines.append(line)
        if line == '.':
            break
    for s in lines[:-1]:
        counts = {chr(ord('a') + i): 0 for i in range(26)}
        for c in s:
            counts[c] += 1
        max_count = max(counts.values())
        candidates = [k for k, v in counts.items() if v == max_count]
        candidates.sort()
        print(' '.join(candidates))

letter_frequency_analyzer()

