n, m = map(int, input().split())
grid = []
for _ in range(n):
    grid.append(list(map(int, input().split())))

if n == 0 or m == 0:
    print(0)
    exit()

dp = [[1 for _ in range(m)] for _ in range(n)]

for i in range(n):
    for j in range(m):
        if i == 0 or j == 0:
            dp[i][j] = 1
        else:
            if grid[i][j] == grid[i-1][j] and grid[i][j] == grid[i][j-1] and grid[i][j] == grid[i-1][j-1]:
                dp[i][j] = min(dp[i-1][j], dp[i][j-1], dp[i-1][j-1]) + 1
            else:
                dp[i][j] = 1

max_size = max(max(row) for row in dp)
print(max_size)
