n, m = map(int, input().split())
grid = []
for _ in range(n):
    row = list(map(int, input().split()))
    grid.append(row)

max_size = min(n, m)

for k in range(max_size, 0, -1):
    for i in range(n - k + 1):
        for j in range(m - k + 1):
            if grid[i][j] == grid[i][j + k - 1] == grid[i + k - 1][j] == grid[i + k - 1][j + k - 1]:
                print(k)
                exit()
