def update_inventory(n, inventory_list, m, orders_list):
    # Initialize inventory dictionary
    inventory = {}
    for item in inventory_list:
        name = item.split()[0]
        quantity = int(item.split()[1])
        inventory[name] = quantity

    # Process each sales order
    for order in orders_list:
        item_name = order[0]
        order_qty = int(order[1])
        if item_name in inventory:
            inventory[item_name] -= order_qty
            if inventory[item_name] < 0:
                inventory[item_name] = 0

    # Output the remaining stock in the original order
    for item in inventory_list:
        print(f"{item} {inventory[item]}")
