def compress(s):
    if not s:
        return ""
    compressed = []
    prev = s[0]
    count = 1
    for c in s[1:]:
        if c == prev:
            count += 1
        else:
            compressed.append((prev, count))
            prev = c
            count = 1
    compressed.append((prev, count))
    compressed_str = ''.join([f"{char}{count}" for char, count in compressed])
    if len(compressed_str) < len(s):
        return compressed_str
    else:
        return s
