def compress(input_string):
    if not input_string:
        return input_string

    result = ""
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            result += f"{current_char}{count}"
            current_char = char
            count = 1
    result += f"{current_char}{count}"

    if len(result) < len(input_string):
        return result
    else:
        return input_string
