def compress(input_string):
    if not input_string:
        return ""

    groups = []
    prev = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == prev:
            count += 1
        else:
            groups.append((prev, count))
            prev = char
            count = 1
    groups.append((prev, count))

    compressed = ''.join([f"{c}{n}" for (c, n) in groups])

    if len(compressed) < len(input_string):
        return compressed
    else:
        return input_string
