def letter_frequency_analyzer(input_lines):
    test_cases = input_lines[:-1]
    result = []

    for s in test_cases:
        freq = {}
        for char in s:
            freq[char] = freq.get(char, 0) + 1

        if not freq:
            continue

        max_freq = max(freq.values())
        max_chars = [char for char, count in freq.items() if count == max_freq]
        max_chars.sort()

        result.append(' '.join(max_chars))

    return result

