from solution import can_form_palindrome

def test_even_length_palindrome():
    assert can_form_palindrome("aabb") == "YES"

def test_no_palindrome_possible():
    assert can_form_palindrome("abc") == "NO"

def test_odd_length_palindrome():
    assert can_form_palindrome("racecar") == "YES"

def test_long_palindrome_possible():
    assert can_form_palindrome("aabbhijkkjih") == "YES"

def test_single_character():
    assert can_form_palindrome("a") == "YES"

def test_two_different_characters():
    assert can_form_palindrome("ab") == "NO"

def test_palindrome_with_odd_char_count():
    assert can_form_palindrome("aaabbbb") == "YES"
