def can_form_palindrome(s):
    from collections import Counter
    count = Counter(s)
    odd_counts = 0
    for char, cnt in count.items():
        if cnt % 2 != 0:
            odd_counts += 1
    return "YES" if odd_counts <= 1 else "NO"

