from collections import deque

def is_path_possible(n, m, blocked_cells):
    blocked_set = set(blocked_cells)

    # Check if start or end is blocked
    if (0, 0) in blocked_set or (n-1, m-1) in blocked_set:
        return False

    queue = deque()
    queue.append((0, 0))
    visited = set()
    visited.add((0, 0))

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    while queue:
        i, j = queue.popleft()

        if i == n-1 and j == m-1:
            return True

        for dx, dy in directions:
            ni, nj = i + dx, j + dy
            if 0 <= ni < n and 0 <= nj < m:
                if (ni, nj) not in blocked_set and (ni, nj) not in visited:
                    visited.add((ni, nj))
                    queue.append((ni, nj))

    return False

