def minFlips(s):
    count0 = 0
    count1 = 0
    for i, c in enumerate(s):
        # Expected character if starting with '0'
        expected0 = '0' if i % 2 == 0 else '1'
        if c != expected0:
            count0 += 1
        # Expected character if starting with '1'
        expected1 = '1' if i % 2 == 0 else '0'
        if c != expected1:
            count1 += 1
    return min(count0, count1)
