def minFlips(S):
    count_a = 0
    count_b = 0
    for i in range(len(S)):
        current = S[i]
        if i % 2 == 0:
            desired_a = '0'
            desired_b = '1'
        else:
            desired_a = '1'
            desired_b = '0'
        if current != desired_a:
            count_a += 1
        if current != desired_b:
            count_b += 1
    return min(count_a, count_b)
