def minFlips():
    S = input().strip()
    flips0 = 0
    flips1 = 0
    for i in range(len(S)):
        expected0 = i % 2
        expected1 = 1 - (i % 2)
        if S[i] != expected0:
            flips0 += 1
        if S[i] != expected1:
            flips1 += 1
    return min(flips0, flips1)
