def minFlips(s):
    min_flips = float('inf')
    for start in [0, 1]:
        flips = 0
        for i, c in enumerate(s):
            if i % 2 == 0:
                expected = str(start)
            else:
                expected = str(1 - start)
            if c != expected:
                flips += 1
        if flips < min_flips:
            min_flips = flips
    return min_flips
