def analyze_sequence(sequences):
    result = []
    for seq in sequences:
        if len(set(seq)) == 1:
            result.append("STEADY")
        else:
            result.append("FLUCTUATING")
    return result

tests:
def test_analyze_sequence_steady_case():
    assert analyze_sequence([[1, 3, 5, 7]]) == ["STEADY"]

def test_analyze_sequence_fluctuating_case():
    assert analyze_sequence([[4, 2, -2, -5]]) == ["FLUCTUATING"]

def test_analyze_sequence_mixed_cases():
    assert analyze_sequence([
        [1, 3, 5, 7],
        [4, 2, -2, -5]
    ]) == ["STEADY", "FLUCTUATING"]

def test_analyze_sequence_edge_case_two_elements():
    assert analyze_sequence([[1, 5]]) == ["STEADY"]

def test_analyze_sequence_same_elements():
    assert analyze_sequence([[3, 3, 3, 3]]) == ["STEADY"]

    assert analyze_sequence([[2, 2]]) == ["STEADY"]
