def maxSum(nums):
    if not nums:
        return 0
    n = len(nums)
    if n == 1:
        return nums[0]
    take = nums[0]
    notake = 0
    for i in range(1, n):
        curr_take = nums[i] + notake
        curr_notake = max(take, notake)
        take, notake = curr_take, curr_notake
    return max(take, notake)
