def maxSum(nums):
    if not nums:
        return 0
    prev_not = 0
    prev_take = nums[0]
    for i in range(1, len(nums)):
        current_take = prev_not + nums[i]
        current_not = max(prev_take, prev_not)
        prev_not, prev_take = current_not, current_take
    return max(prev_take, prev_not)
