def update_inventory(n, inventory_list, m, orders_list):
    inventory = {}
    order_list = []

    for item in inventory_list:
        name, qty = item.split()
        inventory[name] = int(qty)
        order_list.append(name)

    for order in orders_list:
        name, qty = order.split()
        if name in inventory:
            inventory[name] = max(0, inventory[name] - qty)

    for item in order_list:
        print(f"{item} {inventory[item]}")

# Read input
n = int(input())
inventory_list = [input().split() for _ in range(n)]

m = int(input())
orders_list = [input().split() for _ in range(m)]

update_inventory(n, inventory_list, m, orders_list)
