n = int(input())
inventory_list = []
order_list = []
for i in range(n):
    name, qty = input().split()
    inventory_list.append((name, int(qty)))
    order_list.append(i)

m = int(input())
orders = []
for _ in range(m):
    name, qty = input().split()
    qty = int(qty)
    orders.append((name, qty))

current_inventory = {item: qty for item, qty in inventory_list}

for item, qty in orders:
    if item in current_inventory:
        current_inventory[item] -= qty
        if current_inventory[item] < 0:
            current_inventory[item] = 0

for item, qty in inventory_list:
    print(f"{item} {qty}")
