def update_inventory():
    N = int(input())
    inventory = {}
    order_list = []

    for _ in range(N):
        name, qty = input().split()
        inventory[name] = int(qty)
        if name not in order_list:
            order_list.append(name)

    M = int(input())
    for _ in range(M):
        name, qty = input().split()
        qty = int(qty)
        if name in inventory:
            inventory[name] -= qty
            if inventory[name] < 0:
                inventory[name] = 0

    for item in order_list:
        print(f"{item} {inventory[item]}")

update_inventory()
