n = int(input())
thicknesses = list(map(int, input().split()))
T = int(input())

dp = [-1] * (T + 1)
dp[0] = 0

for t in thicknesses:
    for j in range(T, t - 1, -1):
        if dp[j - t] != -1:
            if dp[j] < dp[j - t] + 1:
                dp[j] = dp[j - t] + 1

max_books = 0
for j in range(1, T + 1):
    if dp[j] != -1:
        if j > max_books:
            max_books = j

print(max_books)
