n = int(input())
current_stock = {}
for _ in range(n):
    op = input().strip()
    tokens = op.split()
    op_type = tokens[0]
    id = tokens[1]
    qty = int(tokens[2])
    if op_type == '+':
        current_stock[id] = current_stock.get(id, 0) + qty
    else:
        current_stock[id] = current_stock.get(id, 0) - qty

q = int(input())
for _ in range(q):
    query = input().strip()
    if query in current_stock:
        stock = current_stock[query]
        print("in stock" if stock > 0 else "out of stock")
    else:
        print("out of stock")
