n = int(input())
inventory = {}

for _ in range(n):
    op = input().strip()
    parts = op.split()
    op_type = parts[0]
    rest = parts[1:]
    if len(rest) >= 2:
        id = rest[0]
        qty = rest[1]
    else:
        # According to the problem statement, this case won't occur
        pass
    if op_type == '+':
        current = inventory.get(id, 0)
        current += int(qty)
    else:
        current = inventory.get(id, 0)
        current -= int(qty)
    inventory[id] = current

q = int(input())
for _ in range(q):
    item = input().strip()
    if item in inventory:
        if inventory[item] > 0:
            print("in stock")
        else:
            print("out of stock")
    else:
        print("out of stock")
