def process_inventory(n, operations, q, queries):
    inventory = {}
    for op in operations:
        parts = op.split()
        op_type = parts[0]
        id = parts[1]
        qty = int(parts[2])
        if op_type == '+':
            inventory[id] = inventory.get(id, 0) + qty
        else:
            inventory[id] = inventory.get(id, 0) - qty
    for query in queries:
        item = query
        if item in inventory and inventory[item] > 0:
            print("in stock")
        else:
            print("out of stock")

# Read input
n = int(input())
operations = [input().strip() for _ in range(n)]
q = int(input())
queries = [input().strip() for _ in range(q)]

# Process and output the results
process_inventory(n, operations, q, queries)
