def compress(input_string):
    if not input_string:
        return ""

    prev = None
    count = 0
    result = []

    for char in input_string:
        if char == prev:
            count += 1
        else:
            if prev is not None:
                result.append((prev, count))
            prev = char
            count = 1
    if prev is not None:
        result.append((prev, count))

    compressed = []
    for char, count in result:
        if count == 1:
            compressed.append(char)
        else:
            compressed.append(f"{char}{count}")
    compressed_str = ''.join(compressed)

    if len(compressed_str) < len(input_string):
        return compressed_str
    else:
        return input_string
