def compress(input_string):
    if not input_string:
        return input_string

    runs = []
    current_char = input_string[0]
    current_count = 1

    for char in input_string[1:]:
        if char == current_char:
            current_count += 1
        else:
            runs.append((current_char, current_count))
            current_char = char
            current_count = 1
    runs.append((current_char, current_count))

    compressed = ""
    for char, cnt in runs:
        compressed += char + str(cnt)

    if len(compressed) < len(input_string):
        return compressed
    else:
        return input_string
