def compress(input_string):
    if not input_string:
        return ''

    runs = []
    current_char = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current_char:
            count += 1
        else:
            runs.append((current_char, count))
            current_char = char
            count = 1
    runs.append((current_char, count))

    compressed = ''
    for char, cnt in runs:
        if cnt == 1:
            compressed += char
        else:
            compressed += f"{char}{cnt}"

    return input_string if len(compressed) <= len(input_string) else compressed
