def compress(input_string):
    if not input_string:
        return input_string

    result = ""
    current = input_string[0]
    count = 1

    for char in input_string[1:]:
        if char == current:
            count += 1
        else:
            result += current + str(count)
            current = char
            count = 1
    result += current + str(count)

    if len(result) < len(input_string):
        return result
    else:
        return input_string
