def compress(input_string):
    if not input_string:
        return input_string

    result = []
    current_char = None
    current_count = 0

    for char in input_string:
        if char == current_char:
            current_count += 1
        else:
            if current_char is not None:
                if current_count == 1:
                    result.append(current_char)
                else:
                    result.append(f"{current_char}{current_count}")
            current_char = char
            current_count = 1

    # Add the last group
    if current_char is not None:
        if current_count == 1:
            result.append(current_char)
        else:
            result.append(f"{current_char}{current_count}")

    # Compare lengths
    if len(result) < len(input_string):
        return ''.join(result)
    else:
        return input_string
