from collections import Counter, defaultdict

def reorganizeString(s):
    count = Counter(s)
    max_freq = max(count.values())
    others_total = sum(count.values()) - max_freq

    if max_freq > others_total + 1:
        return ""

    char_counts = sorted(count.items(), key=lambda x: (-x[1], x[0]))
    result = []

    for char, _ in char_counts:
        if not result:
            result.append(char)
        else:
            last_char = result[-1]
            if last_char != char:
                result.append(char)

    return ''.join(result)

