from solution import reorganizeString

def test_example1():
    assert reorganizeString("aab") == "aba"

def test_example2():
    assert reorganizeString("aaab") == ""

def test_single_character():
    assert reorganizeString("a") == "a"

def test_two_characters():
    assert reorganizeString("aa") == ""

def test_no_need_to_reorganize():
    assert reorganizeString("abc") == "abc"

def test_repeating_characters():
    result = reorganizeString("aaabb")
    assert result in ["ababa", "babab"]

def test_large_string():
    s = "a" * 50000 + "b" * 50000
    result = reorganizeString(s)
    # Ensure that no two adjacent characters are the same
    for i in range(len(result) - 1):
        assert result[i] != result[i + 1]
