def minFlips(s):
    flips0 = 0  # Flips needed for pattern starting with '0'
    flips1 = 0  # Flips needed for pattern starting with '1'
    for i in range(len(s)):
        if i % 2 == 0:
            expected0 = '0'
            expected1 = '1'
        else:
            expected0 = '1'
            expected1 = '0'
        if s[i] != expected0:
            flips0 += 1
        if s[i] != expected1:
            flips1 += 1
    return min(flips0, flips1)
