def minFlips(s):
    flips0 = 0
    flips1 = 0
    for i in range(len(s)):
        c = s[i]
        # Calculate flips for starting with '0'
        if i % 2 == 0:
            expected0 = '0'
        else:
            expected0 = '1'
        if c != expected0:
            flips0 += 1
        # Calculate flips for starting with '1'
        if i % 2 == 0:
            expected1 = '1'
        else:
            expected1 = '0'
        if c != expected1:
            flips1 += 1
    return min(flips0, flips1)
